try {
  const items = $input.all();
  const storyContent = items[0].json.message?.content;

  if (!storyContent) throw new Error("Story content not found in input");

  // --- Try parsing the JSON output from assistant ---
  let storyJson = null;
  try {
    storyJson = JSON.parse(storyContent);
  } catch (e) {
    throw new Error("Failed to parse story content as JSON. Ensure assistant output is valid JSON.");
  }

  // --- Validate structure ---
  if (!storyJson.narrative || !Array.isArray(storyJson.pages)) {
    throw new Error("Invalid story format. Missing 'narrative' or 'pages' array.");
  }

  // --- Get form data from Story Parameters node ---
  let formData = null;
  try {
    formData = $('Story Parameters').first().json;
  } catch (e) {
    const inputItem = items.find(item => item.json && (item.json.character_name || item.json.theme));
    if (inputItem) formData = inputItem.json;
  }

  if (!formData || !formData.character_name) {
    throw new Error('Form data not found. Required: character_name, theme, age_range, art_style, moral_lesson');
  }

  const safeFormData = {
    character_name: formData.character_name || 'Main Character',
    theme: formData.theme || 'adventure',
    age_range: formData.age_range || '3-8',
    art_style: formData.art_style || 'watercolor',
    moral_lesson: formData.moral_lesson || 'Be kind and brave'
  };

  // --- Build pages array with metadata ---
  const processedPages = storyJson.pages.map((page) => ({
    page_number: page.page_number,
    text: page.text,
    scene_description: page.illustration || '',
    character_name: safeFormData.character_name,
    theme: safeFormData.theme,
    age_range: safeFormData.age_range,
    art_style: safeFormData.art_style
  }));

  // --- Add title page at start ---
  processedPages.unshift({
    page_number: 0,
    text: safeFormData.character_name,
    is_title_page: true,
    character_name: safeFormData.character_name,
    theme: safeFormData.theme,
    age_range: safeFormData.age_range,
    art_style: safeFormData.art_style,
    scene_description: `Professional children's book cover featuring ${safeFormData.character_name} in their authentic story world. The scene represents the ${safeFormData.theme} theme with rich environmental details that capture the essence of this unique narrative.`
  });

  // --- Add ending page at end ---
  processedPages.push({
    page_number: processedPages.length,
    text: `The End`,
    scene_description: safeFormData.moral_lesson,
    character_name: safeFormData.character_name,
    theme: safeFormData.theme,
    age_range: safeFormData.age_range,
    art_style: safeFormData.art_style
  });

  return [{
    json: {
      success: true,
      story_data: {
        narrative: storyJson.narrative,
        pages: processedPages,
        total_pages: processedPages.length,
        character_name: safeFormData.character_name,
        theme: safeFormData.theme,
        art_style: safeFormData.art_style,
        age_range: safeFormData.age_range,
        dynamic_title: safeFormData.character_name
      }
    }
  }];

} catch (error) {
  return [{
    json: {
      success: false,
      error: error.message,
      message: "Failed to process structured story JSON"
    }
  }];
}

const items = $input.all();

// Map through each item and create a valid JSON element for image_prompt
const prompts = items.map(item => {
  const prompt = item.json.output.image_prompt;

  // Escape quotes and line breaks for valid JSON
  const safePrompt = prompt
    .replace(/\\/g, '\\\\')   // Escape backslashes
    .replace(/"/g, '\\"')     // Escape double quotes
    .replace(/\n/g, '\\n');   // Escape newlines

  return {
    json: { image_prompt: safePrompt }
  };
});

return prompts;

const imagesArr = $input.all();  

const story = $('Structure Story into Pages').first().json.story_data.pages;

// Map through images to create pages array
const pages = imagesArr.map((item, index) => {
    const data = story?.[index];
    const pageNumber = data?.page_number;
    const totalPages = imagesArr.length;

    // Determine page type based on page number
    const page_type = pageNumber === 0 ? 'cover' 
                    : pageNumber === totalPages ? 'end' 
                    : 'story';

    const imageLink = item?.json?.DRIVER_IMAGE_URL || null;

    return {
        page_number: Number(pageNumber),
        page_type: page_type,
        text: data?.text || '',
        image_url: imageLink
    };
});

// Extract filename from story data or generate one
const storyTitle = story?.[0]?.text || 'storybook';
const filename = storyTitle
    .toLowerCase()
    .replace(/[^a-z0-9\s]/g, '') // Remove special characters
    .replace(/\s+/g, '_')         // Replace spaces with underscores
    .substring(0, 50);            // Limit length

// Return single object with complete structure
return [{
    json: {
        pages: pages,
        filename: filename,
        text_overlay_opacity: 0.6
    }
}];

// Get all pages from previous node
const pages = $('Code').item.json.pages || [];

// Extract only the "text" field into an array
const page_texts = pages.map(p => p.text);

// Return in required format
return [{
  json: {
    page_texts
  }
}];

